package patterns;

/**
 * @author Shalini
 *
 */
public class dollerstar1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=1; i<=5; i++) {
	for (int j=1; j<=5; j++) {
	System.out.print(" * ");
	System.out.print("	");
}
for (int j=5; j>=1; j--) {
	System.out.print(" $ ");
	System.out.print("	");
}
System.out.println();
}
}
}
